#include <iostream>#include <stdio.h>#include <vector>#include <string.h>#include <cstring>#include <set>#include <algorithm>#include <map>#include <queue>#include <set>#include <cmath>using namespace std;typedef long long ll;#define y1 fjlwekfconst int mod = 1e9 + 7;const double eps = 1e-13;const int N = 1000005;vector<int> a[N];vector<int> b[N];int main(){    int n, m;    cin >> n >> m;    for(int i = 0; i < n; ++i){        int x;        scanf("%d", &x);        a[x].push_back(i);    }    for(int i = 0; i < N; ++i){        a[i].push_back(N);    }    for(int i = 0; i < N; ++i){        b[i].assign(a[i].size(), 0);        for(int j = a[i].size() - 2; j >= 0; --j){            if(a[i][j] + 1 == a[i][j + 1]){                b[i][j] = b[i][j + 1] + 1;            }        }    }    for(int i = 0; i < m; ++i){        int l, r, x;        scanf("%d %d %d", &l, &r, &x);        l--; r--;        int pos = lower_bound(a[x].begin(), a[x].end(), l) - a[x].begin();        if(a[x][pos] != l){            printf("%d\n", l + 1);        }        else{            int id = a[x][pos] + b[x][pos] + 1;            if(id > r){                printf("-1\n");            }            else{                printf("%d\n", id + 1);            }        }    }    return 0;}