#include <bits/stdc++.h> using namespace std; #define MAXN 1000000 int main () {	int n,m;    int v[MAXN], p[MAXN];     scanf("%d %d", &n, &m);     v[0] = p[0]= 0;     for(int i = 1; i<= n; i++) {        scanf("%d", &v[i]);        if(v[i]==v[i-1]) p[i]=p[i-1];        else p[i]=i;    }     for(int i = 0; i < m; i++) {		int l,r,x;        scanf("%d %d %d", &l, &r, &x);        int aux = r;        while(1) {			if(v[aux]!=x) break;			else {				aux = p[aux]-1;				if(aux<l) {					aux = -1;					break;				}			}        }        printf("%d\n", aux);    }}